const rawOutput = $json.output;

const match = rawOutput.match(/"pedaco1":(.|\n|\r)*"pedaco[0-9]+":\s*"(https?:[^\"]*)"/);
const pedacosText = match ? match[0] : '';

const fixedJsonString = `{${pedacosText.replace(/\n/g, ',')}}`;

const jsonInput = JSON.parse(fixedJsonString);

const respostasArray = [];
if (jsonInput.pedaco1) respostasArray.push(jsonInput.pedaco1);
if (jsonInput.pedaco2) respostasArray.push(jsonInput.pedaco2);

return respostasArray.map(resposta => ({ json: { resposta: resposta } }));

const response = $input.all()[0].json;
const html = String(response.response || response.body || response.data || response);

const extractText = (pattern, html) => {
  const match = String(html).match(pattern);
  return match ? match[1].trim() : null;
};

const cleanText = (text) => text ? text.replace(/\s+/g, ' ').replace(/&nbsp;/g, ' ').trim() : null;

let price = null;
const normalPriceMatch = html.match(/<div class="elementor-widget-container">\s*<p class="price">[\s\S]*?<span class="woocommerce-Price-amount amount">[\s\S]*?<bdi>[\s\S]*?R\$(?:&nbsp;|\s)*([\d.,]+)/i);
const discountPriceMatch = html.match(/<span class="price">[\s\S]*?<ins[^>]*>[\s\S]*?<span class="woocommerce-Price-amount amount">[\s\S]*?<bdi>[\s\S]*?R\$(?:&nbsp;|\s)*([\d.,]+)/i);
const jsonLdMatch = html.match(/"price":"([\d.]+)"/i);

const priceValue = normalPriceMatch?.[1] || discountPriceMatch?.[1] || jsonLdMatch?.[1];
price = priceValue ? `R$ ${priceValue.replace('.', ',')}` : null;

const productImages = [];
const imageMatches = html.matchAll(/<a[^>]*href="(https?:\/\/[^"]+?\.(?:jpg|jpeg|png|gif|webp))"[^>]*>/gi);
for (const match of imageMatches) {
  if (match[1] && !productImages.includes(match[1])) {
    productImages.push(match[1]);
  }
}

const productData = {
  productInfo: {
    name: cleanText(html.match(/<h1\s+class="product_title[^>]*>([\s\S]*?)<\/h1>/i)?.[1]),
    price: price,
    images: productImages
   }
 };

return productData;

