if (!matches || matches.length === 0) {
  throw new Error("Nenhum pedaco encontrado no texto.");
}

const jsonBody = matches.map(match => match[0]).join(',');
const jsonFinal = `{${jsonBody}}`;

let dados;
try {
  dados = JSON.parse(jsonFinal);
} catch (erro) {
  throw new Error("Erro ao fazer parse do JSON: " + erro.message);
}

const saidas = [];
for (const chave of Object.keys(dados).sort()) {
  saidas.push({
    json: {
      mensagem: dados[chave]
    }
  });
}

return saidas;

