const now = new Date();

const saoPauloDate = now.toLocaleString("pt-BR", { timeZone: "America/Sao_Paulo" });

const oneHourLater = new Date(now.getTime() + 60 * 60 * 1000);

const saoPauloISO = now.toLocaleString("sv-SE", { timeZone: "America/Sao_Paulo", hour12: false }).replace(" ", "T") + "-03:00";
const saoPauloMaisUmaHoraISO = oneHourLater.toLocaleString("sv-SE", { timeZone: "America/Sao_Paulo", hour12: false }).replace(" ", "T") + "-03:00";

const dataDaqui7Dias = new Date();
dataDaqui7Dias.setDate(dataDaqui7Dias.getDate() + 7);
const saoPauloDataDaqui7Dias = dataDaqui7Dias.toLocaleString("pt-BR", { timeZone: "America/Sao_Paulo" });

const data7DiasAntes = new Date();
data7DiasAntes.setDate(data7DiasAntes.getDate() - 7);
const saoPauloData7DiasAntes = data7DiasAntes.toLocaleString("pt-BR", { timeZone: "America/Sao_Paulo" });

return [
  {
    json: {
      dataSaoPaulo: saoPauloDate,
      dataDaqui7Dias: saoPauloDataDaqui7Dias,
      data7DiasAntes: saoPauloData7DiasAntes,
      dataSaoPauloISO: saoPauloISO,
      dataSaoPauloMaisUmaHoraISO: saoPauloMaisUmaHoraISO,
    },
  },
];

